#coding=utf-8#生成的output.html文件在用Chrome打开时中显示乱码, 为Chrome默认编码问题, 解决如下#在Chrome中选中"自定义及控制"(即右上角三杠形)按钮 -> 更多工具 -> 编码 -> utf-8#__author__ = 'xing'# 一下三句来解决IDE内中文乱码问题import sysreload(sys)sys.setdefaultencoding("utf-8")class HtmlOutputer(object):    def __init__(self):        self.datas = []    def collect_data(self, data):        if data is None:            return        self.datas.append(data)    def output_html(self):        fout = open('output.html', 'w')        fout.write("<html>")        fout.write("<body>")        fout.write("<table>")        for data in self.datas:            fout.write("<tr>")            fout.write("<td>%s</td>" % data['url'])            # fout.write("<td>%s</td>" % data['title'].encode('utf-8'))            fout.write("<td>%s</td>" % data['title'].decode('utf-8'))            fout.write("<td>%s</td>" % data['summary'].encode('utf-8'))            fout.write("</tr>")        fout.write("</table>")        fout.write("</body>")        fout.write("</html>")        fout.close()